const imgGrid=document.querySelector(".img-grid");async function fetchPictures(){(await(await fetch("https://cdn.jsdelivr.net/gh/SaulDoesCode/resources/imgs.txt")).text()).split("\n\n").map((t=>{let[e,n]=t.trim().split("\n");return e.endsWith(",")&&(e=e.slice(0,-1)),[e,n]})).filter((([t,e])=>t&&e)).randomize().map((([t,e])=>{const n=document.createElement("article"),i=document.createElement("img");i.setAttribute("src","https://"+t);const s=document.createElement("p");s.textContent=e,n.append(i,s),imgGrid.appendChild(n)}))}Array.prototype.randomize=function(){const t=[],e=this.length;for(;t.length!=e;){const e=Math.floor(Math.random()*this.length);t.push(this[e]),this.splice(e,1)}return this.splice(0,this.length,...t),this},fetchPictures();